import List from "./components/List";
import { useState } from "react";

function App() {
  const [content, setContent] = useState({
    title: "",
    cont: "",
    isDone: false,
  });
  const [users, setUsers] = useState([]);

  const onChangeHandler = (e) => {
    setContent((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
      isDone: false,
    }));
  };

  const onClickArrayHandler = () => {
    const { title, cont, isDone } = content;

    setUsers([
      ...users,
      {
        title,
        cont,
        isDone,
      },
    ]);

    setContent({ title: "", cont: "", isDone: false });
  };

  const onClickIsDoneHandler = () => {
    setContent((pre) => ({
      ...pre,
      isDone: !pre.isDone,
    }));
  };

  return (
    <div className="App">
      제목:
      <input
        type="text"
        name="title"
        value={content.title}
        onChange={onChangeHandler}
      />
      내용:
      <input
        type="text"
        name="cont"
        value={content.cont}
        onChange={onChangeHandler}
      />
      <button onClick={onClickArrayHandler}>등록하기</button>
      {users.map((ele, idx) => {
        return (
          <List
            user={ele}
            key={idx}
            onClickIsDoneHandler={onClickIsDoneHandler}
          />
        );
      })}
    </div>
  );
}

export default App;
