import List from "./components/List";
import { useState } from "react";

function App() {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const arr = [];
  const onClickSubmitHandler = ({ title, content }) => {
    arr.push({
      tit: { title },
      cont: { content },
    });
    console.log("arr", arr);
  };

  return (
    <div className="App">
      제목 :
      <input
        type="text"
        value={title}
        onChange={(pre) => setTitle(pre.target.value)}
      />
      내용 :
      <input
        type="text"
        value={content}
        onChange={(pre) => setContent(pre.target.value)}
      />
      {/* <button onClick={onClickSubmitHandler}>등록하기</button> */}
      <List title={title} content={content} />
    </div>
  );
}

export default App;
