import { useState } from "react";

function App() {
  const [content, setContent] = useState({ title: "", cont: "" });
  const arr = [];
  const onClickSubmitHandler = (e) => {
    setContent((pre) => ({
      ...pre,
      [name]: value,
    }));
  };

  return (
    <div className="App">
      제목 :
      <input
        type="text"
        value={content.title}
        onChange={(pre) => setTitle(pre.target.value)}
      />
      내용 :
      <input
        type="text"
        value={content.cont}
        onChange={(pre) => setContent(pre.target.value)}
      />
      <button onClick={onClickSubmitHandler}>등록하기</button>
      <ul>
        {arr.map((ele) => {
          return (
            <li key={ele.tit}>
              <List title={ele.tit} content={ele.cont} />
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default App;
