import List from "./components/List";
import { useState } from "react";

function App() {
  const [content, setContent] = useState({
    id: 0,
    title: "",
    cont: "",
    isDone: false,
  });
  const [users, setUsers] = useState([]);

  const onChangeHandler = (e) => {
    setContent((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
      isDone: false,
    }));
  };

  const onClickArrayHandler = () => {
    const { id, title, cont, isDone } = content;

    setUsers([
      ...users,
      {
        id,
        title,
        cont,
        isDone,
      },
    ]);

    setContent((pre) => ({
      id: pre.id + 1,
      title: "",
      cont: "",
      isDone,
    }));
  };

  const onClickIsDoneHandler = (id) => {
    // setContent((pre) => ({
    //   ...pre,
    //   isDone: !pre.isDone,
    // }));
    setUsers((pre) =>
      pre.map((user) => (user.id === id ? !user.isDone : user.isDone))
    );
    console.log("되는거니?", content);
  };

  const onClickDeleteHandler = (id) => {
    setUsers((pre) => pre.filter((user) => user.id !== id));
  };
  console.log("users", users);
  return (
    <div className="App">
      제목:
      <input
        type="text"
        name="title"
        value={content.title}
        onChange={onChangeHandler}
      />
      내용:
      <input
        type="text"
        name="cont"
        value={content.cont}
        onChange={onChangeHandler}
      />
      <button onClick={onClickArrayHandler}>등록하기</button>
      {users.map((ele) => {
        return (
          <List
            user={ele}
            key={ele.id}
            onClickIsDoneHandler={onClickIsDoneHandler}
            onClickDeleteHandler={onClickDeleteHandler}
          />
        );
      })}
    </div>
  );
}

export default App;
