import List from "./components/List";
import { useState } from "react";

function App() {
  const [content, setContent] = useState({ title: "", cont: "" });
  const [users, setUsers] = useState([]);

  const onChangeHandler = (e) => {
    setContent((pre) => ({
      ...pre,
      [e.target.name]: e.target.value,
    }));
  };

  const onClickArrayHandler = () => {
    const { title, cont } = content;

    setUsers([
      ...users,
      {
        title,
        cont,
      },
    ]);

    setContent(...users, {
      title: "",
      cont: "",
    });
  };
  console.log("users", users);

  return (
    <div className="App">
      제목 :
      <input type="text" name="title" onChange={onChangeHandler} />
      내용 :
      <input type="text" name="cont" onChange={onChangeHandler} />
      <button onClick={onClickArrayHandler}>등록하기</button>
      {users.map((ele) => {
        return <List users={ele} key={ele.title} />;
      })}
    </div>
  );
}

export default App;
